  </div>
</template>

<script>
export default {
    data(){
        return {
            cart: false,
            profile: false,
        }
    },
    computed: {
        cartItems(){
            return this.$store.state.cartItems;
        },
        totalQuantity(){
            return this.$store.state.cartItems.reduce((acc, val) => acc + val.quantity, 0);
        }
    },
    methods: {
        checkoutHandler(){
            if(this.cartItems.length <= 0) return;
            this.cart = false;
            this.profile = false;
            this.$router.push('checkout');
        },
        dropdownOpen(item){
            if(item === 'cart'){
                this.cart = !this.cart;
                this.profile = false;
            }else{
                this.profile = !this.profile;
                this.cart = false;
            }
        }
    }
}
</script>